package language.basics;

public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// TODO Auto-generated method stub
		// 1. Temperature of a city in degrees Celsius: 25.5
		float temperature = 25.5f;
		System.out.println("Temperature of city in degrees Celsius : " + temperature);

		// 2. Whether a customer has placed an order: true or false
		boolean order = true;
		System.out.println("The order status is : " + order);

		// 3. Person's phone number: "123-456-7890"
		String PhoneNumber = "123-456-7890";
		System.out.println("The customer's phone number is : " + PhoneNumber);

		// 4. Amount of money in a customer's bank account: 1000.50
		float Amount = 100;
		System.out.println("Amount of money in a customer's bank account : " + Amount);

		// 5. Person's email address: "john.doe@example.com"
		String email = "john.doe@example.com";
		System.out.println("Person's email address is : " + email);

		// 6. Coordinates of a location (latitude, longitude): 37.7749, -122.4194
		float latitudelocation = 37.7749f;
		float longitudelocation = 122.4194f;
		System.out.println("Coordinates of a latitude and longitude location are : " + latitudelocation + " , "
				+ longitudelocation);

		// 7. Person's marital status: true or false
		boolean maritalstatus = true;
		System.out.println("Person's marital status : " + maritalstatus);

		// 8.Person's occupation: "Software Engineer"
		String occupation = "Software Engineer";
		System.out.println("Person's occupation : " + occupation);

		// 9. Person's favourite colour: "Blue"
		String colour = "Blue";
		System.out.println("Person's favourite colour :" + colour);

		// 10.Current year: 2023
		short year = 2023;
		System.out.println("Current year : " + year);

		// 11.Number of followers on a social media platform: 1,000,000
		String followers = "1,000,000";
		System.out.println("Number of followers on a social media platform : " + followers);

		// 12.Rating of a movie: 7.5
		float rating = 7.5f;
		System.out.println("Movie rating : " + rating);

		// 13.Person's blood type: 'A'
		char blood = 'A';
		System.out.println("Person's blood type : " + blood);

		// 14.Title of a book: "To Kill a Mockingbird"
		String title = "To Kill a Mockingbird";
		System.out.println("Title of a book : " + title);

		// 15.Number of employees in a company: 500
		short employees = 500;
		System.out.println("Number of employees in a company : " + employees);

		// 16.Time of an event: 2:30 PM
		String time = "2:30 PM";
		System.out.println("Time of an event : " + time);

		// 17.Name of a country: "United States"
		String name = "United States";
		System.out.println("Name of a country : " + name);

		// 18.Person's eye color: "Brown"
		String eye = "Brown";
		System.out.println("Person's eye color : " + eye);

		// 19.Person's birthplace: "New York City"
		String birthplace = "New York City";
		System.out.println("Person's birthplace : " + birthplace);

		// 20.Distance between two cities: 200.5
		float distance = 200.5f;
		System.out.println("Distance between two cities : " + distance);

	}

}
